DROP VIEW IF EXISTS `v_system_company_department_user`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `v_system_company_department_user` AS select `tc_system_company`.`name` AS `company_name`,`tc_system_department`.`name` AS `department_name`,`tc_system_user`.`login_name` AS `login_name`,`tc_system_user`.`name` AS `userr_name`,`tc_system_user`.`company_id` AS `company_id`,`tc_system_user`.`dep_id` AS `dep_id`,`tc_system_user`.`id` AS `id` from ((`tc_system_company` join `tc_system_department`) join `tc_system_user`) where ((`tc_system_company`.`id` = `tc_system_user`.`company_id`) and (`tc_system_department`.`id` = `tc_system_user`.`dep_id`)) ;

