CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `v_system_datapermission` AS select `tc_system_datapermission_rule`.`resource_id` AS `resource_id`,`tc_system_datapermission_rule`.`name` AS `name`,`tc_system_datapermission_rule`.`remark` AS `remark`,`tc_system_datapermission_rule_detail`.`property_id` AS `property_id`,`tc_system_datapermission_rule_detail`.`type` AS `type`,`tc_system_datapermission_rule_detail`.`value` AS `value`,`tc_system_datapermission_rule_detail`.`sort_number` AS `sort_number`,`tr_system_datapermission_rule_user`.`company_id` AS `company_id`,`tr_system_datapermission_rule_user`.`department_id` AS `department_id`,`tr_system_datapermission_rule_user`.`role_id` AS `role_id`,`tr_system_datapermission_rule_user`.`user_id` AS `user_id`,`tc_system_datapermission_rule_detail`.`rule_id` AS `rule_id`,`tc_system_resources`.`code` AS `code`,`tc_system_resources`.`description` AS `description`,`tc_system_datapermission_rule_detail`.`property_name` AS `property_name`,`tc_system_datapermission_rule_detail`.`is_all` AS `is_all`,`tc_system_datapermission_rule_detail`.`operator` AS `operator`,`tc_system_datapermission_rule_detail`.`id` AS `id`,`tc_system_datapermission_rule_detail`.`condition_group` AS `condition_group`,`tc_system_resources`.`url` AS `url` from (((`tc_system_datapermission_rule` join `tc_system_datapermission_rule_detail`) join `tr_system_datapermission_rule_user`) join `tc_system_resources`) where ((`tc_system_datapermission_rule`.`enable` = 0) and (`tc_system_datapermission_rule`.`id` = `tc_system_datapermission_rule_detail`.`rule_id`) and (`tc_system_datapermission_rule_detail`.`rule_id` = `tr_system_datapermission_rule_user`.`rule_id`) and (`tc_system_datapermission_rule`.`resource_id` = `tc_system_resources`.`id`) and (`tc_system_resources`.`code` is not null)) ;

