INSERT INTO `tc_system_protalmodule` VALUES ('12', '极性图', 'containerDiv', '$(\'#containerDiv\').highcharts({\r\n        \r\n	    chart: {\r\n	        polar: true\r\n	    },\r\n	    \r\n	    title: {\r\n	        text: \'Highcharts Polar Chart\'\r\n	    },\r\n	    \r\n	    pane: {\r\n	        startAngle: 0,\r\n	        endAngle: 360\r\n	    },\r\n	\r\n	    xAxis: {\r\n	        tickInterval: 45,\r\n	        min: 0,\r\n	        max: 360,\r\n	        labels: {\r\n	        	formatter: function () {\r\n	        		return this.value + \'°\';\r\n	        	}\r\n	        }\r\n	    },\r\n	        \r\n	    yAxis: {\r\n	        min: 0\r\n	    },\r\n	    \r\n	    plotOptions: {\r\n	        series: {\r\n	            pointStart: 0,\r\n	            pointInterval: 45\r\n	        },\r\n	        column: {\r\n	            pointPadding: 0,\r\n	            groupPadding: 0\r\n	        }\r\n	    },\r\n	\r\n	    series: [{\r\n	        type: \'column\',\r\n	        name: \'Column\',\r\n	        data: [8, 7, 6, 5, 4, 3, 2, 1],\r\n	        pointPlacement: \'between\'\r\n	    }, {\r\n	        type: \'line\',\r\n	        name: \'Line\',\r\n	        data: [1, 2, 3, 4, 5, 6, 7, 8]\r\n	    }, {\r\n	        type: \'area\',\r\n	        name: \'Area\',\r\n	        data: [1, 8, 2, 7, 3, 6, 4, 5]\r\n	    }]\r\n	});', 'elusive-heart', '0', '2015-04-02 16:02:41', '1', '2015-04-02 16:03:29', '1');
INSERT INTO `tc_system_protalmodule` VALUES ('13', '日程图', 'calendar', 'var currentLangCode = \'zh-cn\';\r\n$(\'#calendar\').fullCalendar(\'destroy\');\r\n\r\nfunction renderCalendar(){\r\n  //初始化calendar\r\n  $(\'#calendar\').fullCalendar({\r\n    //是否安装主题\r\n    theme:true,\r\n    header: {\r\n      left: \'prev,next today\',\r\n      center: \'title\',\r\n      right: \'month,agendaWeek,agendaDay\'\r\n    },\r\n    defaultDate: new Date(),\r\n    lang: currentLangCode,\r\n    buttonIcons: false, // show the prev/next text\r\n    weekNumbers: true,\r\n    selectable: false,\r\n    selectHelper: true,\r\n    select: function(start, end) {\r\n\r\n      var title = prompt(\'Event Title:\');\r\n      var eventData;\r\n      if (title) {\r\n        eventData = {\r\n          title: title,\r\n          start: start,\r\n          end: end\r\n        };\r\n        $(\'#calendar\').fullCalendar(\'renderEvent\', eventData, true); // stick? = true\r\n      }\r\n      $(\'#calendar\').fullCalendar(\'unselect\');\r\n    },\r\n    editable: false,\r\n    droppable: false, // this allows things to be dropped onto the calendar\r\n    drop: function() {\r\n      // is the \"remove after drop\" checkbox checked?\r\n      if ($(\'#drop-remove\').is(\':checked\')) {\r\n        // if so, remove the element from the \"Draggable Events\" list\r\n        $(this).remove();\r\n      }\r\n    },\r\n    eventLimit: true, // allow \"more\" link when too many events\r\n    events: function(start, end, timezone, callback){\r\n      var leftYear=start._d.getFullYear();\r\n      var leftMonth=start._d.getMonth()+1;\r\n      var leftDay=start._d.getDate();\r\n      var rightYear=end._d.getFullYear();\r\n      var rightMonth=end._d.getMonth()+1;\r\n      var rightDay=end._d.getDate();\r\n      $.ajax({\r\n        url: dynamicURL+\'schedule/listMaintainSchedule?leftYear=\'+leftYear+\'&leftMonth=\'+leftMonth+\'&leftDay=\'+leftDay+\'&rightYear=\'+rightYear+\'&rightMonth=\'+rightMonth+\'&rightDay=\'+rightDay,\r\n        type:\'POST\',\r\n        dataType: \'json\',\r\n        data: {\r\n          start: start.unix(),\r\n          end: end.unix()\r\n        },\r\n        success: function(doc) {\r\n          var events = [];\r\n          $(doc).each(function() {\r\n            events.push({\r\n              id:$(this).attr(\'id\'),\r\n              title: $(this).attr(\'title\'),\r\n              start: $(this).attr(\'start\'),\r\n              end:$(this).attr(\'end\'),\r\n              allDay:$(this).attr(\'allDay\'),\r\n              color:$(this).attr(\'color\'),\r\n              textColor:$(this).attr(\'textColor\'),\r\n              content:$(this).attr(\'content\'),\r\n              url:$(this).attr(\'url\')\r\n            });\r\n          });\r\n          callback(events);\r\n        },\r\n        error: function() {\r\n          alert(\'there was an error while fetching events!\');\r\n        },\r\n        color: \'#ffa35f\',   \r\n        textColor: \'white\' \r\n      })\r\n    }\r\n  });\r\n}\r\nrenderCalendar();\r\n', 'elusive-glass', '0', '2015-06-24 16:11:05', '1', '2015-07-21 14:52:34', '1');
INSERT INTO `tc_system_protalmodule` VALUES ('14', '不停航施工日程图', 'calendarWeek', 'var currentLangCode = \'zh-cn\';\r\n$(\'#calendarWeek\').fullCalendar(\'destroy\');\r\n\r\nfunction renderCalendar(){\r\n  //初始化calendar\r\n  $(\'#calendarWeek\').fullCalendar({\r\n    //是否安装主题\r\n    theme:true,\r\n    header: {\r\n      left: \'prev,next today\',\r\n      center: \'title\',\r\n      right: \'basicWeek\'\r\n    },\r\n    defaultView:\'basicWeek\',\r\n    defaultDate: new Date(),\r\n    lang: currentLangCode,\r\n    buttonIcons: false, // show the prev/next text\r\n    weekNumbers: true,\r\n    selectable: false,\r\n    selectHelper: false,\r\n    //contentHeight:0,\r\n    editable: false,\r\n    droppable: false, // this allows things to be dropped onto the calendar\r\n    eventLimit: true, // allow \"more\" link when too many events\r\n    eventMouseover: function(event, jsEvent, view){                \r\n    	showDetail(event, jsEvent);                   \r\n    },  \r\n    eventMouseout: function(event, jsEvent, view){  \r\n    	$(\'#tip\').remove();  \r\n    },  \r\n    events: function(start, end, timezone, callback){\r\n      var leftYear=start._d.getFullYear();\r\n      var leftMonth=start._d.getMonth()+1;\r\n      var leftDay=start._d.getDate();\r\n      var rightYear=end._d.getFullYear();\r\n      var rightMonth=end._d.getMonth()+1;\r\n      var rightDay=end._d.getDate();\r\n      $.ajax({\r\n        url: dynamicURL+\'schedule/listConstructionSchedule?leftYear=\'+leftYear+\'&leftMonth=\'+leftMonth+\'&leftDay=\'+leftDay+\'&rightYear=\'+rightYear+\'&rightMonth=\'+rightMonth+\'&rightDay=\'+rightDay,\r\n        type:\'POST\',\r\n        dataType: \'json\',\r\n        data: {\r\n          start: start.unix(),\r\n          end: end.unix()\r\n        },\r\n        success: function(doc) {\r\n          var events = [];\r\n          $(doc).each(function() {\r\n            events.push({\r\n              id:$(this).attr(\'id\'),\r\n              title: $(this).attr(\'title\'),\r\n              start: $(this).attr(\'start\'),\r\n              end:$(this).attr(\'end\'),\r\n              allDay:$(this).attr(\'allDay\'),\r\n              color:$(this).attr(\'color\'),\r\n              textColor:$(this).attr(\'textColor\'),\r\n              content:$(this).attr(\'content\'),\r\n              url:$(this).attr(\'url\')\r\n            });\r\n          });\r\n          callback(events);\r\n        },\r\n        error: function() {\r\n          alert(\'there was an error while fetching events!\');\r\n        },\r\n        color: \'#ffa35f\',   \r\n        textColor: \'white\' \r\n      })\r\n    }\r\n  });\r\n}\r\nrenderCalendar();\r\nfunction showDetail(obj, e){  \r\n  var eInfo = \'<div id=\"tip\"><ul>\';  \r\n  eInfo += \'<li>项目描述：\' + obj.title+\'</li>\';  \r\n  eInfo += \'<li>开始时间：\'+moment(new Date(obj.start)).format(\"YYYY-MM-DD\") +\'</li>\';\r\n  eInfo += \'<li>结束时间：\'+moment(new Date(obj.end)).format(\"YYYY-MM-DD\")+ \'</li>\';\r\n  eInfo += \'<li>施工监管：\'+ obj.content + \'<br/> </li>\';  \r\n  eInfo += \'</ul></div>\';  \r\n  $(eInfo).appendTo($(\'body\'));  \r\n  $(\'#tip\').css({\"opacity\":\"0.4\", \"display\":\"none\", \"left\":(e.pageX + 20) + \"px\", \"top\":(e.pageY + 10) + \"px\"}).fadeTo(600, 0.9);  \r\n  //鼠标移动效果  \r\n  $(\'.fc-event-inner\').mousemove(function(e){  \r\n    $(\'#tip\').css({\'top\': (e.pageY + 10), \'left\': (e.pageX + 20)});  \r\n  });  \r\n}   ', 'elusive-road', '0', '2015-11-06 19:34:43', '1', '2015-11-09 22:04:53', '1');
INSERT INTO `tc_system_report` VALUES ('1', '数据列表报表', '<table id=\"fileDatagrid\" class=\"datagrid-custom-resize\" ></table>', '$(\'#fileDatagrid\').datagrid({\r\n	            url: dynamicURL+\'/system/file/list\',\r\n	            title: \'附件列表\',\r\n	            pagination: true,\r\n	            rownumbers : true,\r\n		    fit:true,\r\n	            custom_fit: true,\r\n	            custom_height: 5,\r\n	            singleSelect:false, \r\n	            idField: \'id\',\r\n	            columns: [[\r\n	                 {field: \'contentType\', title: \'类型\',width:50},\r\n	                 {field: \'module\', title: \'模块\',width:100},\r\n	                 {field: \'fileName\', title: \'文件名\',width:400},\r\n	                 {field: \'convertFileSize\', title: \'文件大小\',align: \'right\',width:100},\r\n	                 {field: \'gmtCreate\', title: \'上传时间\',align: \'center\',width:200},\r\n	                 {field: \'downloadUrl\',title: \'操作\',align: \'center\',width:50, \r\n	                	 formatter: function (value, rec)\r\n	                	 { return \'<a name=\"download\" href=\"#\" onclick=\"download(\'+rec.id+\')\"></a>\';}\r\n	                 },\r\n	            ]],\r\n	            onLoadSuccess:function(data){\r\n	            	$(\"a[name=\'download\']\").linkbutton({ plain:true, iconCls:\'easyui-icon-download\' });\r\n	            }\r\n	        });', null, 'system', '1', '2015-01-30 11:31:17', '1', '2015-01-30 16:13:42', '1');
