stSchedule?leftYear=\'+leftYear+\'&leftMonth=\'+leftMonth+\'&leftDay=\'+leftDay+\'&rightYear=\'+rightYear+\'&rightMonth=\'+rightMonth+\'&rightDay=\'+rightDay,\r\n		        type:\'POST\',\r\n		        dataType: \'json\',\r\n		        data: {\r\n		        	start: start.unix(),\r\n	                end: end.unix()\r\n		        },\r\n		        success: function(doc) {\r\n	                var events = [];\r\n	                $(doc).each(function() {\r\n	                    events.push({\r\n	                    	id:$(this).attr(\'id\'),\r\n	                        title: $(this).attr(\'title\'),\r\n	                        start: $(this).attr(\'start\'),\r\n	                        end:$(this).attr(\'end\'),\r\n	                        allDay:$(this).attr(\'allDay\'),\r\n                          	color:$(this).attr(\'color\'),\r\n                            textColor:$(this).attr(\'textColor\'),\r\n                          	content:$(this).attr(\'content\')\r\n	                    });\r\n	                });\r\n	                callback(events);\r\n	            },\r\n		        error: function() {\r\n		            alert(\'there was an error while fetching events!\');\r\n		        },\r\n		        color: \'#3a87ad\',   \r\n		        textColor: \'white\' \r\n				})\r\n		    }\r\n		});\r\n		}\r\n		renderCalendar();\r\n', null, 'file', '13', '2015-03-11 15:36:56', '1', '2015-06-24 16:00:34', '1');
