INSERT INTO `tm_sms_security_assurance` VALUES ('555', '6f5dd793-1359-4d6c-9f89-ce1c804ae989', null, '<p>\r\n	4.3安全检查车岗位设置序列表</p>\r\n<p>\r\n	&nbsp;&nbsp; 安全检查车安全检查岗位包括安全检查车X光机操作和安全检查车开箱检查岗位，其岗位设置见表4</p>\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" width=\"252\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:62px;\">\r\n				<p>\r\n					岗位编码</p>\r\n			</td>\r\n			<td style=\"width:79px;\">\r\n				<p>\r\n					岗位名称</p>\r\n			</td>\r\n			<td style=\"width:111px;\">\r\n				<p>\r\n					上岗要求</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:62px;\">\r\n				<p>\r\n					000301</p>\r\n			</td>\r\n			<td style=\"width:79px;\">\r\n				<p>\r\n					安全检查车X光机操作</p>\r\n			</td>\r\n			<td style=\"width:111px;\">\r\n				<p>\r\n					国家安检职业资格证书四级以上</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:62px;\">\r\n				<p>\r\n					000302</p>\r\n			</td>\r\n			<td style=\"width:79px;\">\r\n				<p>\r\n					安全检查车开箱检查</p>\r\n			</td>\r\n			<td style=\"width:111px;\">\r\n				<p>\r\n					国家安检职业资格证书四级以上</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '150', '3', '539', '2016-11-10 16:20:55', '1', '2016-11-10 16:20:55', null);
INSERT INTO `tm_sms_security_assurance` VALUES ('556', '468ddef3-3621-4292-8b58-99e4ee821b63', null, '<p>\r\n	第二十二条:民用机场管理机构和公共航空运输企业应当按照本规定要求制定本单位安检从业人员年度培训工作计划等,并报民航地区管理局备案。</p>\r\n', '121', '3', '540', '2016-11-10 16:28:29', '1', '2016-11-10 16:28:29', null);
INSERT INTO `tm_sms_security_assurance` VALUES ('557', '1480f5e4-4c4f-43da-8fd2-185963287f22', null, '<p>\r\n	第二十三条:民用机场管理机构和公共航空运输企业应当建立包括民航安检从业人员在职教育培训年度培训工作计划、年度培训工作评估、培训教员、培训记录等内容的培训工作档案。培训工作档案保存时间不少于3年。</p>\r\n', '123', '3', '541', '2016-11-10 16:28:41', '1', '2016-11-10 16:28:41', null);
