INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('1', 'cd043945-f13a-4fae-81ac-7c30e9073198', '1', '1', '81', '583', '防跑道侵入', '584', '道面巡检工作制度', '587', '《民用机场运行安全管理规定》第五十七条、《青岛流亭国际机场运行安全管理规定》第一百三十八条', '588', '<p>\r\n	（1）制度至少应当包括：一是每日巡视检查的次数不应少于4次，每月对跑道、滑行道的清扫每月不应少于1次；二是跑道、滑行道巡视检查的通报程序；三是巡视检查人员与塔台管制员联系的标准用语；四是巡视检查跑道过程中发生紧急情况的处置程序。</p>\r\n', '1', '不符合', null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('2', '04d291ac-0358-45e7-8920-67af6ea41925', '1', '1', '81', '583', '防跑道侵入', '585', '巡场员巡场实际操作程序', '589', '《民用运输机场运行安全管理规定》第五十九条及第七十条、《青岛流亭国际机场运行安全管理规定》第一百四十一条', '590', '<p>\r\n	（1）巡视前应对车辆进行全面检查。巡视检查的车辆应当安装黄色旋转灯标。</p>\r\n', '2', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('3', 'd6965e95-bbaa-48a7-96a7-220d356c030e', '1', '1', '81', '583', '防跑道侵入', '585', '巡场员巡场实际操作程序', '589', '《民用运输机场运行安全管理规定》第五十九条及第七十条、《青岛流亭国际机场运行安全管理规定》第一百四十一条', '591', '<p>\r\n	（2）巡场人员除驾驶员外，应至少有一名专业检查人员。</p>\r\n', '3', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('4', '8c1e1767-e5ae-47c7-adfd-1eae8ea1b09a', '1', '1', '81', '583', '防跑道侵入', '585', '巡场员巡场实际操作程序', '589', '《民用运输机场运行安全管理规定》第五十九条及第七十条、《青岛流亭国际机场运行安全管理规定》第一百四十一条', '592', '<p>\r\n	（3）在检查期间始终开启黄色旋转灯标。检查人员应当穿反光背心或外套。</p>\r\n', '0', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('5', '46899620-8e0d-4cd8-ab68-4c2addb986eb', '1', '1', '81', '583', '防跑道侵入', '585', '巡场员巡场实际操作程序', '589', '《民用运输机场运行安全管理规定》第五十九条及第七十条、《青岛流亭国际机场运行安全管理规定》第一百四十一条', '593', '<p>\r\n	（4）按照工作单卡使用标准用语与塔台对话。检查人员在进入跑道、滑行道之前，应当得到塔台管制员的许可。进入该区域时，应当直接报告塔台管制员。退出后，应当直接报告塔台管制员。</p>\r\n', '2', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('6', '9bbb4f5f-f0b7-47ef-86fb-764f7699ba03', '1', '1', '81', '583', '防跑道侵入', '585', '巡场员巡场实际操作程序', '589', '《民用运输机场运行安全管理规定》第五十九条及第七十条、《青岛流亭国际机场运行安全管理规定》第一百四十一条', '594', '<p>\r\n	（5）航前全面检查时，必须对跑道全宽度表面状况进行详细检查。起降期间中间检查的区域应当至少包括跑道边灯以内的区域。</p>\r\n', '2', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('7', '46d5550f-1dfe-4bab-9398-abfeac33a8c1', '1', '1', '81', '583', '防跑道侵入', '586', '跑道检查台账', '595', '《民用运输机场运行安全管理规定》第五十九条、《青岛国际机场与民航青岛空管站地面保障工作协议》', '596', '<p>\r\n	（1）跑道日巡视检查频次达到4次以上。其中，每日开放前检查1次为道面检查，其余3次为航空器起降过程中间检查。</p>\r\n', '2', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('8', 'd252959d-edbc-4cc0-998e-e249101507c0', '1', '1', '81', '583', '防跑道侵入', '586', '跑道检查台账', '595', '《民用运输机场运行安全管理规定》第五十九条、《青岛国际机场与民航青岛空管站地面保障工作协议》', '597', '<p>\r\n	（2）检查日志包括检查开始时间、结束时间、检查人员姓名、飞行区场地情况记录在检查日志中。</p>\r\n', '2', null, null, '2016-12-01 15:07:36', '1', '2016-12-01 15:07:36', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('9', 'b4f3a407-52e7-4f87-b34e-83daae552132', '2', '1', '81', '599', '飞行区道面(含土面)管理', '600', '飞行区道面抢修预案', '601', '《民用机场运行安全管理规定》第四十一条', '602', '<p>\r\n	包括跑、滑道及机坪道面的破损类型、部位对应相应的抢修措施。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('10', 'e7d4a36c-4be7-4af7-96a6-ffc280fe3955', '2', '1', '81', '599', '飞行区道面(含土面)管理', '603', '跑滑道检查制度', '604', '《民用机场运行安全管理规定》第五十七条', '605', '<p>\r\n	每日巡视检查次数不少于4次；时间至少包括开放前、航空器起降过程中间。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('11', '402344b5-be12-4482-9bd6-1e537ecae441', '2', '1', '81', '599', '飞行区道面(含土面)管理', '603', '跑滑道检查制度', '604', '《民用机场运行安全管理规定》第五十七条', '606', '<p>\r\n	有跑、滑道巡视检查的通报程序。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('12', '5f632a65-cc07-4025-9396-3b77d243a3dd', '2', '1', '81', '599', '飞行区道面(含土面)管理', '603', '跑滑道检查制度', '604', '《民用机场运行安全管理规定》第五十七条', '607', '<p>\r\n	有巡视检查人员与塔台管制员联系的标准用语。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('13', '9926d3a9-a84c-47b7-b776-a919f0151c3a', '2', '1', '81', '599', '飞行区道面(含土面)管理', '603', '跑滑道检查制度', '604', '《民用机场运行安全管理规定》第五十七条', '608', '<p>\r\n	有巡视检查跑道过程中发生紧急情况的处置程序。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
