INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('15', '4d35537f-5613-4a80-b53d-dbfc85034752', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '612', '<p>\r\n	巡场前对车辆全面检查，包括车辆黄色警示灯是否正常工作。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('16', '46fde8a1-b2c6-4ecc-8d0a-1e51c232cde8', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '613', '<p>\r\n	对跑道检查时，检查方向与航空器起飞或着陆方向相反。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('17', '7d450ac6-1c19-43d1-92d2-a935cf006d01', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '614', '<p>\r\n	在检查期间开启黄色旋转灯标，检查人员穿反光背心或外套。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('18', 'b8bd2e50-7bac-4d5f-880b-79c95e14d511', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '615', '<p>\r\n	驾车检查时，除驾驶员外还应当有1名专业的检查人员在车辆内。车速不得超过45公里/小时。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('19', 'af40b442-13c7-4cca-8a4a-9dc8f84baf83', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '616', '<p>\r\n	巡视检查期间，检查人员配备无线电对讲机，并在相应无线电波道上保持守听。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('20', '3c7699c1-c558-453c-9dc8-7f913c8d84f0', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '617', '<p>\r\n	下车检查时，检查人员离开车辆的距离不得超过100米，并随身携带对讲机，检查车辆禁止熄火。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('21', '7f3af885-9630-4c47-9c8b-f5d036001341', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '618', '<p>\r\n	按照工作单卡使用标准用语与塔台对话。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('22', '93295c2a-3724-4719-9a22-062a2e9d75f2', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '619', '<p>\r\n	检查人员在进入跑道、滑行道之前，得到塔台管制员的许可。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('23', 'dc89293f-41b7-4852-87ab-575c3b6f36f3', '2', '1', '81', '599', '飞行区道面(含土面)管理', '610', '巡场员岗位操作规程', '611', '《民用运输机场运行安全管理规定》第五十九条、第七十条、第七十一条、七十二条；《青岛流亭国际机场运行安全管理规定》第一百四十一条', '620', '<p>\r\n	进入或退出该区域时，报告塔台管制员。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('24', 'cd89f32f-b6b3-4b2f-9b76-12912ba77f9c', '2', '1', '81', '599', '飞行区道面(含土面)管理', '621', '密实度测试工作记录', '622', '《民用机场运行安全管理规定》第五十二条', '623', '<p>\r\n	升降带平整区和跑道端安全地区的土质密实度不得低于87%（重型击实法）。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('25', 'e6cb3c30-8de3-4b91-bd6d-785d570d9ff4', '2', '1', '81', '599', '飞行区道面(含土面)管理', '621', '密实度测试工作记录', '622', '《民用机场运行安全管理规定》第五十二条', '624', '<p>\r\n	对升降带平整区和跑道端安全地区的碾压和密实度测试，每年不得少于2次。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('26', 'b48a594c-39d3-42ef-be40-0f48d467da8e', '2', '1', '81', '599', '飞行区道面(含土面)管理', '625', '跑道巡查工作记录', '626', '《民用机场运行安全管理规定》第五十九条', '627', '<p>\r\n	每日跑道开放前，对跑道进行1次全面检查，至少包括跑道前宽度表面状况详细检查。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('27', '032b0250-4c2f-40bd-acbd-0b8bcfd4fb0c', '2', '1', '81', '599', '飞行区道面(含土面)管理', '625', '跑道巡查工作记录', '626', '《民用机场运行安全管理规定》第五十九条', '628', '<p>\r\n	对跑道进行不少于3次的中间检查，在航空器起降集中时段前，应进行1次中间检查，中间检查应至少包括跑道边灯以内的区域。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('28', '45dcd716-1b81-42f4-b64b-5209b86fd903', '2', '1', '81', '599', '飞行区道面(含土面)管理', '625', '跑道巡查工作记录', '626', '《民用机场运行安全管理规定》第五十九条', '629', '<p>\r\n	跑道道面损坏加剧或雨后遇连续高温天气时，适当增加检查次数。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('29', '592bc8ae-6fd1-4c63-8d33-f2c30088d50b', '2', '1', '81', '599', '飞行区道面(含土面)管理', '630', '道面清扫工作记录', '631', '《民用机场运行安全管理规定》第六十条', '632', '<p>\r\n	对跑道、滑行道的清扫每月不应少于1次。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
