INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('31', 'de3b6a23-21c7-49a7-98cd-1bf90cb59bed', '2', '1', '81', '599', '飞行区道面(含土面)管理', '634', '跑道、滑行道、机坪、升降带、跑道端安全区、飞行区围界、巡场路、排水系统巡视检查工作记录', '635', '《民用机场运行安全管理规定》第六十二至六十四条、第六十八条', '636', '<p>\r\n	每日至少对滑行道、机坪、升降带、跑道端安全地区、飞行区围界、巡场路巡视检查1次。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('32', '39210256-26e5-44f8-857e-0218d3b2a028', '2', '1', '81', '599', '飞行区道面(含土面)管理', '634', '跑道、滑行道、机坪、升降带、跑道端安全区、飞行区围界、巡场路、排水系统巡视检查工作记录', '635', '《民用机场运行安全管理规定》第六十二至六十四条、第六十八条', '637', '<p>\r\n	每季度对跑道、滑行道和机坪的铺筑面进行1次全面的步行检查，当道面破损处较多或者破损加剧时，增加步行检查的次数。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('33', '0e2fcbc1-86f4-4ab1-9052-b6a974d69d29', '2', '1', '81', '599', '飞行区道面(含土面)管理', '634', '跑道、滑行道、机坪、升降带、跑道端安全区、飞行区围界、巡场路、排水系统巡视检查工作记录', '635', '《民用机场运行安全管理规定》第六十二至六十四条、第六十八条', '638', '<p>\r\n	雨季来临前，对排水系统进行全面检查。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('34', '49dcb538-6108-4a25-b7de-6fbeaa2b2928', '2', '1', '81', '599', '飞行区道面(含土面)管理', '634', '跑道、滑行道、机坪、升降带、跑道端安全区、飞行区围界、巡场路、排水系统巡视检查工作记录', '635', '《民用机场运行安全管理规定》第六十二至六十四条、第六十八条', '639', '<p>\r\n	飞行区内排水系统应当保持完好、畅通。暴雨期间，随时巡查排水系统；雨后对升降带和跑道端安全地区进行检查。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('35', '7796d68e-94b2-4d10-8e96-b5071f84c184', '2', '1', '81', '599', '飞行区道面(含土面)管理', '634', '跑道、滑行道、机坪、升降带、跑道端安全区、飞行区围界、巡场路、排水系统巡视检查工作记录', '635', '《民用机场运行安全管理规定》第六十二至六十四条、第六十八条', '640', '<p>\r\n	当出现大风及其他不利气候条件时，增加对飞行区的巡视检查次数。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
