INSERT INTO `tm_sms_security_assurance_bill_detail` VALUES ('40', '997048e6-b464-4eb3-a1c0-2e60468e3ff0', '2', '1', '81', '599', '飞行区道面(含土面)管理', '644', '摩擦系数测试、跑道除胶工作记录', '645', '《民用机场运行安全管理规定》第四十三条、第八十条、第八十一条、第八十二条、第八十四条、第八十五条', '649', '<p align=\"justify\">\r\n	出现下列情况后，立即进行跑道摩擦系数测试：</p>\r\n<p align=\"justify\">\r\n	（1）遇大雨或者跑道结冰、积雪；&nbsp;</p>\r\n<p align=\"justify\">\r\n	（2）在跑道上施洒除冰液或颗粒；&nbsp;</p>\r\n<p>\r\n	（3）航空器偏出、冲出跑道。</p>\r\n', '2', null, null, '2016-12-01 16:26:17', '1', '2016-12-01 16:26:17', null);
